import React ,{ Component } from 'react';
import {connect} from 'react-redux';
import {Link} from 'react-router-dom';

class ArticlesList extends Component{
	render() {
		return(
			<div className="col-md-9 column content_container">
				{
					this.props.articlesList.map((item,index) => (
						<div className="post" key={index}>
							<h2 className="post-title">
								<Link to={'/articlesdetail/' + item.get('id')}>
									{item.get('title')}
								</Link>
							</h2>
							<div className="post-meta">
								<svg className="icon" aria-hidden="true">
									<use xlinkHref="#icon-calendar"></use>
								</svg>&nbsp;{item.get('time')}
							</div>
							<div className="post-content">{item.get('desc')}</div>
							<p className="readmore"><a href="/">阅读更多</a></p>
						</div>
					))
				}

				<nav className="page-navigator">
					<Link className="prev" rel="prev" href="/">上一页</Link>
					<Link className="page-number" href="/">1</Link>
					<Link className="page-number current">2</Link>
					<Link className="page-number" href="/">3</Link>
					<Link className="page-number" href="/">4</Link>
					<Link className="next" rel="next" href="/" onClick={() => getMoreList(this.props.page)}>下一页</Link>
				</nav>
			</div>
		)
	}
}

const mapStateToProps = (state) => ({
	articlesList: state.getIn(['home','articlesList'])
});

const mapDisPatchToProps = (dispatch) => ({
	getMoreList(page) {
		dispatch(actionCreators.getMoreList(page))
	}
});

export default connect(mapStateToProps,mapDisPatchToProps)(ArticlesList);
