import { fromJS } from 'immutable';
//import * as constants from './constants';

const defaultState = fromJS({
	articlesList: [{
		id: 1,
		title: "单页应用和单页应用（vue）的比较",
		time: "2018/10/8",
		desc: "多页应用：不断请求html网页。单页应用：刚开始请求一次，后面通过js渲染不断将dom元素从页面上删除和添加",
	},{
		id: 2,
		title: "单页应用和单页应用（vue）的比较",
		time: "2018/10/8",
		desc: "多页应用：不断请求html网页。单页应用：刚开始请求一次，后面通过js渲染不断将dom元素从页面上删除和添加"
	},{
		id: 3,
		title: "UI自动化测试--Puppeteer再探",
		time: "2018/10/7",
		desc: "自动化测试自动化测试自动化测试自动化测试自动化测试自动化测试自动化测试自动化测试自动化测试自动化测试自动化测试自动化测试自动化测试自动化测试自动化测试自动化测试"
	}],
	category:[],
	linkList: [],
	articlesdetail: [{
		id: 1,
		title: "高级爬虫--Puppeteer初探",
		time: "Aug 17, 2017",
		content: "Puppeteer是一个node库，他提供了一组用来操纵Chrome的API，理论上使用它可以做任何Chrome可以做的事"
	}]
});

export default (state = defaultState, action) => {
	switch(action.type) {
		case 'get_info' :
			return state.merge({
				category:fromJS(action.category),
				linkList:fromJS(action.linkList)
			})
		default :
			return state;
	}
}
