import { fromJS } from 'immutable';
import * as constants from './constants';

const defaultState = fromJS({
	articlesList: [{
		id: 1,
		title: "遇见你，是我的快乐",
		time: "2018/10/8",
		desc: "遇见你 是我的快乐 不管你相不相信 我都想把这份喜悦 画在美丽的心上 遇见你，是我的快乐 不用觉得我夸大其词 我是想把这份喜悦 挂在如月的嘴角 ...",
	},{
		id: 2,
		title: "遇见你，是我的快乐",
		time: "2018/10/8",
		desc: "遇见你 是我的快乐 不管你相不相信 我都想把这份喜悦 画在美丽的心上 遇见你，是我的快乐 不用觉得我夸大其词 我是想把这份喜悦 挂在如月的嘴角 ..."
	}]
});

export default (state = defaultState, action) => {
	switch(action.type) {
		case constants.NAV_CLICK :
			return state.set('active',false);
		default :
			return state;
	}
}
