//import { fromJS } from 'immutable';

const defaultState = {
	author: '犹游由我待',
	Juzi: '学习学习学习啊',
	active: true
};

export default (state = defaultState, action) => {
	switch(action.type) {
		case itemClick :
			return state.set('active',false);
		default :
			return state;
	}
	
}
